package Homework;

public class TtemperatureTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int temperature = 15;
		if (temperature < 32) {
			System.out.println("water will freeze with temperature" + temperature);
		} else {
			System.out.println("False");
		}
		int temperature2 = 34;
		if (temperature2 > 32) {
			System.out.println("water will  not freeze with temperature " + temperature2);
		}
	}

}
