package com.syntax.class02;

public class AssigmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*Assigment Operators =
		 * AssigmentOperators=assigning value 
		 * AssigmentOperator first than assigning value
		 * Arithmetical operators= +,-,*,/,%
		 * 
		 */
int num1=40;
int num2=20;
		System.out.println(num1+num2);
		System.out.println(num1-num2);
		System.out.println(num1/num2);
		
		int sum,sub,mult,div;
		sum=num1+num2;
		System.out.println(sum);//60
		sub=num1-num2;
		System.out.println(sub);//20
		mult=num1*num2;		
		System.out.println(mult);//800
		div=num1/num2;
		System.out.println(div);//2
		
		double sum1, sub1, mult1, div1;
		double num3=9.99;
		double num4=3.50;
		
		sum1=num3+num4;
		sub1=num3-num4;
		mult1=num3*num4;
		div1=num3/num4;
		
		System.out.println(sum1);
		System.out.println(sub1);
		System.out.println(mult1);
		System.out.println(div1);
		
		int a=10;
		int b=3;
		int result=a/b;
				System.out.println(result);//3
				
				double c=10.0;
				double d=3.0;
				double result2=c/d;
				System.out.println(result2);//=3.33333333333333 (up to 14-16)
				
				float e =10.0f;
				float f=3.0f;
				float result3= e/f;
			System.out.println(result3);//3.333333 //(up to 6-7)
			
				
				
				
				
				
		
		
		
		
		
	}
}