package com.syntax.class07;

public class Increcement2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
	
			
			int r=100;
			while (r>=1) {
				if(r%2!=0)
				System.out.print(r+" ");
				r--;
		    }
			int a =99;
			while (a>=1)
				{System.out.println(a+" ");
				a-=2;
				}
	}
	

	}

